-- Khách Hàng
CREATE TABLE KHACHHANG
(
	Id_KH INT IDENTITY(300, 1) PRIMARY KEY,
	Ten_KH NVARCHAR(50) NOT NULL,
	Diachi_KH NVARCHAR(50) NOT NULL,
	Phone_KH NVARCHAR(50) NOT NULL unique,
)
GO
--Sản phẩm
CREATE TABLE SANPHAM
(
	--Tăng tự động ID từ 200 và mỗi lần tăng 1
	Id_SP INT IDENTITY(200, 1) PRIMARY KEY,
	Ten_SP NVARCHAR(50) Default N'',
	Soluong_SP INT check(Soluong_SP > 0),
	GiaNhap_SP FLOAT check(GiaNhap_SP > 0),
	Giaban_SP FLOAT NOT NULL,
	Hinhanh_SP IMAGE NOT NULL,
	Ghichu_SP NVARCHAR(100) NOT NULL
)
GO
--Nhân viên
CREATE TABLE NHANVIEN
(
	Id_NV INT IDENTITY(100, 1) PRIMARY KEY,
	Hoten_NV NVARCHAR(50) NOT NULL,
	Diachi_NV NVARCHAR(50) NOT NULL,
	Sodienthoai_NV NVARCHAR(50) NOT NULL Unique,
	Email_NV NVARCHAR(50) NOT NULL,
	Vaitro_NV BIT NOT NULL,
	Tinhtrang_NV BIT NOT NULL,
	Matkhau_NV NVARCHAR(50) NOT NULL
)
GO
--Hóa đơn
CREATE TABLE HOADON
(
	Id_HD INT IDENTITY(400, 1) PRIMARY KEY,
	Id_NV INT NOT NULL,
	ID_KH INT NOT NULL,
	Ngaythanhtoan DATETIME DEFAULT SYSDATETIME(),
	Tongtien FLOAT NOT NULL,
	FOREIGN KEY (Id_NV) REFERENCES NHANVIEN(Id_NV),
	FOREIGN KEY (ID_KH) REFERENCES KHACHHANG(ID_KH),
)
GO
--Chi tiết hóa đơn
CREATE TABLE CHITIETHOADON
(
	Id_SanPham INT NOT NULL,
	Id_Hoadon INT NULL,
	Soluong INT check(Soluong > 0),
	Giaban FLOAT NOT NULL,
	PRIMARY KEY(ID_Sanpham),
	FOREIGN KEY (Id_SanPham) REFERENCES SANPHAM(Id_SP),
	FOREIGN KEY (Id_Hoadon) REFERENCES HOADON(Id_HD),
)
GO

