-- Thay đổi mật khẩu 
CREATE PROC ChangePassword
@email NVARCHAR(50), @oldPassword NVARCHAR(50), @newPassword NVARCHAR(50)
AS BEGIN
DECLARE @password NVARCHAR(50), @result bit
SELECT @password = Matkhau_NV FROM NHANVIEN WHERE Email_NV = @email
IF @password = @oldPassword
BEGIN
    UPDATE NHANVIEN SET Matkhau_NV = @newPassword WHERE Email_NV = @email
	SET @result = 1
END
ELSE SET @result = 0
SELECT @result
END
GO

--Phân quền đăng nhập 
CREATE PROC Login
@email NVARCHAR(50), @password NVARCHAR(50)
AS BEGIN
DECLARE @status BIT
IF EXISTS(SELECT * FROM NHANVIEN WHERE Email_NV = @email AND Matkhau_NV = @password)
	SET @status = 1
ELSE
	SET @status = 0
SELECT @status
END
