--CURSOR LẤY DANH SÁCH SẢN PHẨM -- 1
DECLARE CS_SANPHAM CURSOR
FOR
	SELECT Id_SP, Ten_SP, Soluong_SP, GiaNhap_SP, Giaban_SP, Hinhanh_SP, Ghichu_SP 
	FROM SANPHAM
OPEN CS_SANPHAM
FETCH NEXT FROM CS_SANPHAM
WHILE(@@FETCH_STATUS = 0)
BEGIN
	FETCH NEXT FROM CS_SANPHAM
END

CLOSE CS_SANPHAM
DEALLOCATE CS_SANPHAM

--CURSOR CẬP NHẬP SỐ LƯỢNG CỦA SẢN PHẨM --5
DECLARE CS_GETSANPHAM CURSOR
FOR
	SELECT Id_SP, Soluong_SP FROM SANPHAM
OPEN CS_GETSANPHAM
DECLARE @Id_SP INT, @Soluong_SP INT
FETCH NEXT FROM CS_GETSANPHAM INTO @Id_SP, @Soluong_SP
WHILE(@@FETCH_STATUS = 0)
BEGIN
	UPDATE SANPHAM
	SET Soluong_SP = @Soluong_SP + 1
	WHERE Id_SP = @Id_SP
	FETCH NEXT FROM CS_GETSANPHAM INTO @Id_SP, @Soluong_SP
END

CLOSE CS_GETSANPHAM
DEALLOCATE CS_GETSANPHAM

GO

--CURSOR CẬP NHẬP TỔNG TIỀN --6
DECLARE CS_UPDATE_HOADON CURSOR
FOR
	SELECT Id_SanPham, Id_Hoadon, Soluong, Giaban FROM CHITIETHOADON
OPEN CS_UPDATE_HOADON
DECLARE @Id_SanPham INT,@Id_Hoadon INT,@Soluong INT,@Giaban FLOAT
FETCH NEXT FROM CS_UPDATE_HOADON INTO @Id_SanPham,
									  @Id_Hoadon,
									  @Soluong,
									  @Giaban
WHILE(@@FETCH_STATUS = 0)
BEGIN
	UPDATE HOADON
	SET Tongtien = (@Soluong*@Giaban)
	WHERE Id_HD = @Id_Hoadon
	FETCH NEXT FROM CS_UPDATE_HOADON INTO @Id_SanPham,
									  @Id_Hoadon,
									  @Soluong,
									  @Giaban
END
CLOSE CS_UPDATE_HOADON
DEALLOCATE CS_UPDATE_HOADON

GO

--CURSOR UPDATE TÊN SẢN PHẨM BẢNG SẢN PHẨM -- 7
DECLARE CS_UPDATE_TENSP CURSOR
FOR
	SELECT Id_SP, Ten_SP, Ghichu_SP FROM SANPHAM
OPEN CS_UPDATE_TENSP
DECLARE @Id_SP_SP INT, @Ten_SP NVARCHAR(50), @Ghichu_SP NVARCHAR(50)
FETCH NEXT FROM CS_UPDATE_TENSP INTO @Id_SP_SP, @Ten_SP, @Ghichu_SP
WHILE(@@FETCH_STATUS = 0)
BEGIN
	UPDATE SANPHAM
	SET Ten_SP = @Ten_SP +'-'+ @Ghichu_SP
	WHERE Id_SP = @Id_SP_SP
	FETCH NEXT FROM CS_UPDATE_TENSP INTO @Id_SP_SP, @Ten_SP, @Ghichu_SP
END

CLOSE CS_UPDATE_TENSP
DEALLOCATE CS_UPDATE_TENSP

GO

